              // not logged in yet, might be processing incoming hash/token 
              let hash = WINDOW.location.hash;

              let newToken = (hash || '').split('token=');
              console.log('newToken1', newToken);
              if(newToken.length > 1){
                newToken = newToken[newToken.length-1];
              } else {
                newToken = null;
              }

              // clear hash 
