              if(!newToken){ 
                // no token, redirect to login 
                console.log('No token (before login!)');

                this.setState({
                  renderLogin: true
                });

                // prevents more components loading...
                return false;


              } else {

                // token in hash, processing login (after redirect) 
                console.log('newToken2', newToken);

                let passphrase = newToken;
                try {
                  let successfulLogin = await this.loginWithPassphrase(passphrase);
                }catch(err){
                  return resolve(false);
                }

                // finished upgrading local token 
                return resolve(true);


              }

            }

