        @autobind
        loginWithPassphrase(passphrase){
          return new Promise(async (resolve,reject)=>{

            // Authenticate with External Second 
            // - make authenticated requests to a Second (necessary for Learning?) 
            //   - should have ALL requests authenticated 
            // universe.setupExternalSecond();

            try {

              // get connection target 
              let OwnerSecondExternalIdentityNode = this.props.state.OwnerSecondExternalIdentityNode;
              if(!OwnerSecondExternalIdentityNode){
                console.error('Uh oh, missing OwnerSecondExternalIdentityNode');
                return reject(false);
              }

              // Run the entire authentication handshake with the pre-existing ExternalIdentity Node 
              // - creates external_identity on remote 
              // - updates external_identity locally ( in browser), to include "token" (encrypted {} on remote) 
              let authExternalIdentityNode2 = await universe.loadAndRunCapability('IdentifyInitiate',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'start',
                  options: {
                    externalIdentityPublicKey: OwnerSecondExternalIdentityNode.data.publicKey,
                    // authToken: 'eeaac41bdb1b717d137a53779db97457:07e0fbe19e72c3ff7e329fc293bcf5b51d780c4c3c60f80403b2fea8f30d0daa5e4891dbdd6c57d9844622078a681853f31202d0632f0ee95031c9369a60708ddb6220e248e3c2ec6b3bb3c00eb2425aea922351d7128ef333ea5fe6a8ad139c1978759e29b057db1e655f2fe7c8962e' // "learn" 
                  }
                }
              });

              console.log('Finished IdentifyInitiate2. Starting IdentifyActAsInternalUser');

              // Update the local external_identity.data.token 
              // - doesnt change anything on the remote! 
              // - only changes the local encrypted "token" to included an internalUserId (gets new token from remote. token not stored anywhere) 
              let actAsNode = await universe.loadAndRunCapability('IdentifyActAsInternalUser',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'start',
                  options: {
                    externalIdentityPublicKey: OwnerSecondExternalIdentityNode.data.publicKey,
                    internalUserPassphrase: passphrase
                  }
                }
              });
              // Check type
              if(actAsNode.type.indexOf('identity_upgrade_finish:') !== 0){
                throw "Failed Login"
              }
              console.log('Finished Upgrading local identity with ActAs. actAsNode Response', actAsNode);
            }catch(err){
              console.error('Failed upgrading ActAs after login');
              WINDOW.alert('Failed login, please try again');
              return reject(false); // TODO: error message 
            }

            // re-fetch OwnerSecondExternalIdentityNode after being updated! 
            // - otherwise, overwrites "actas" changes 
            await this.fetchDefaultNodes();
            OwnerSecondExternalIdentityNode = this.props.state.OwnerSecondExternalIdentityNode;

            // Update OwnerSecondExternalIdentityNode.data.isLoggedIn
            OwnerSecondExternalIdentityNode.data.isLoggedIn = true;
            await universe.updateNode(OwnerSecondExternalIdentityNode);

            this.props.setState({
              OwnerSecondExternalIdentityNode
            });

            console.log('Updated OwnerSecondExternalIdentityNode');

            return resolve(true);

          });
        }

