(()=>{
  return new Promise(async (resolve,reject)=>{
    try {

      const AceEditor = universe.AceEditor;
      const ReactResizeDetector = universe.ReactResizeDetector;
      const RecreateChildOnPropsChange = universe.RecreateChildOnPropsChange;

      class EmbeddedComponent extends React.Component {
        constructor(props){
          super(props);
        }

        render(){
          return (
            <div>
              embeddedComponenthere
            </div>
          )
        }
      }

      let ErrorComponent = (name) => {
        return class ErrorComponent extends React.Component {
          constructor(props){
            super(props);
          }
          render(){
            return (
              <div>
                ErrorLoading: {name || 'unknown'}
              </div>
            )
          }
        }
      }

      class mycomponent extends React.Component {
        constructor(props){
          super(props);

          this.state = {
            myPosts: [],
            // groups: [], // from parent
            // allExternalConnections: [], // from parent
            postText: '',
            postGroupNodeId: '', 
            isPosting: false,
          }
        }

        componentDidMount(){

          console.log('Loaded MainPostsComponent');

          this.startup();

        }

        @autobind
        async startup(){
          // this.fetchGroups();
          // this.fetchExternalConnections();

          this.fetchFromRemoteForIdentity(this.props);

        }

        @autobind
        fetchFromRemoteForIdentity(props){
          // fetching data from a remote (syndicated-to) Second 
          // - data for a Single person's thread 

          this.setState({
            fetchingRemote: true
          });

          props = props || this.props;

          console.log('fetchFromRemoteForIdentity.New Props', props);
          console.log('this.props.state.OwnerSecondExternalIdentityNode', this.props.state.OwnerSecondExternalIdentityNode);

          let {
            viewingIdentity,
            searchIdentity
          } = props;

          let viewIdentity = viewingIdentity;

          console.log('viewingIdentity',viewingIdentity, searchIdentity);

          // 
          return new Promise(async (resolve,reject)=>{

            try {


              let response = await universe.loadAndRunCapability('TalkToSecond',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'send',
                  options: {
                    ExternalIdentityNode: this.props.state.OwnerSecondExternalIdentityNode,
                    RequestNode: {
                      type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
                      data: {
                        actions: [

                          {
                            matchActionType: 'identify_via_token:0.0.1:local:237823783g2123',
                            dataForAction: {
                              type: 'string:...',
                              data: this.props.state.OwnerSecondExternalIdentityNode.data.token
                            }
                          },

                          {
                            matchActionType: 'remote_run_action_sequence_authorized:Qm329f83289hsf',
                            dataForAction: {
                              type: 'remote_action_sequence_input:Qmf8329j29388923h8f22f',
                              data: {
                                // who to search (by identity, token) 
                                // action_sequence array 

                                identity: searchIdentity,
                                // internalId: ExternalIdentityNode._id,
                                // publicKey: ExternalIdentityNode.data.publicKey,

                                actions: [

                                  // identify_via_token is added automatically to the beginning of the sequence
                                  // {
                                  //   matchActionType: 'identify_via_token:0.0.1:local:237823783g2123',
                                  //   dataForAction: {
                                  //     type: 'string:...',
                                  //     data: this.props.state.OwnerSecondExternalIdentityNode.data.token
                                  //   }
                                  // },

                                  // Part 1/3
                                  // Fetch MY threads (where I am the author) 
                                  // - of a "post:Qmfdlsj" type 
                                  // - use the returned thread_ref to find ALL the related (by me or not) Posts (and other data too, I suppose) 
                                  {
                                    matchActionType: 'search_shared_data:Qmksdjhfwekjhdds24f',
                                    dataForAction: {
                                      // type: 'standard_query_request:0.0.1:local:65723f2khfds',
                                      type: 'shared_data_request:Qmsdfljsdj',
                                      data: {
                                        // filter on the ENTIRE shared_node (NOT just on the shared_node data) 
                                        dataFilter: {
                                          'data.signer': viewIdentity,
                                          'data.data.author' : viewIdentity
                                        },
                                      }
                                    }
                                  },

                                  // Part 2/3 
                                  // - reduce above (from requestCache) to thread_ref, and use in next step! 
                                  {
                                    matchActionType: 'reduce_search:Qmlfjsdlsf',
                                    dataForAction: {
                                      // type: 'standard_query_request:0.0.1:local:65723f2khfds',
                                      type: 'reduce_request:Qmsf89h923sf',
                                      data: {

                                        type: 'code_miniverse:0.0.1:local:2782fh8823s23',
                                        data: {
                                          code: `(()=>{
                                            return new Promise(async (resolve,reject)=>{
                                              // Part 2/3
                                              // Reduce for next step 
                                              // - setting requestVariables too 

                                              let requestCache = INPUT.data.requestCache; 
                                              // await universe.getRequestCache();

                                              let lastRequestResults;
                                              try {
                                                lastRequestResults = requestCache.keyvalue.request1; // 1st/2nd request (depending on authtoken)
                                              }catch(err){
                                                console.error(err);
                                              }

                                              lastRequestResults = lastRequestResults || [];

                                              let threadRefs = lastRequestResults.map(req=>{
                                                return req.data.data.thread_ref;
                                              });


                                              // return inputNode;
                                              return resolve({
                                                type: 'array_of_refs:Qmdslfjsdlf22',
                                                data: {
                                                  refs: threadRefs
                                                }
                                              });

                                            });
                                          })()`
                                        }
                                      },

                                    }
                                  },


                                  // Part 3/3
                                  // Fetch data for thread_refs (includes My results too) 
                                  // - of a "post:Qmfdlsj" type 
                                  // - use the returned thread_ref to find ALL the related (by me or not) Posts (and other data too, I suppose) 
                                  {
                                    matchActionType: 'search_shared_data:Qmksdjhfwekjhdds24f',
                                    dataForAction: {
                                      // type: 'standard_query_request:0.0.1:local:65723f2khfds',
                                      type: 'shared_data_request:Qmsdfljsdj',
                                      data: {
                                        dataFilter: {
                                          'data.data.thread_ref': {
                                            // $in: "[[$$test.test1$$]]",
                                            $in: "[[$$(INPUT.data.requestCache.keyvalue.request2.data.refs)$$]]"
                                          }
                                        },
                                      }
                                    }
                                  },

                                  // TODO: clear results I dont care about (map->reduce...kill all the map results!) 

                                ]

                              }
                            }
                          }
                        ]

                      }
                    }
                  }
                }
              });

              // let nodes = universe.lodash.find(response.data.actionResponses[0].data[0].nodes,{type: 'code:0.0.1:local:32498h32f2'});
              console.log('FullExternalResponse', response);


              // let nodes = universe.lodash.find(response.data.actionResponses[0].data[0].nodes,{type: 'code:0.0.1:local:32498h32f2'});
              let nodes = response.data.actionResponses[1].data.actionResponses[3].data;

              console.log('Posts:', nodes.length, nodes);

              // Create trees from nodes (in_reply_to) 
              // - get all of my unique Posts 
              let myPosts = universe.lodash.uniq(nodes.filter(node=>{
                // filter 
                return node.data.data.author == viewIdentity;
              }), node=>{
                // uniq filter 
                return node.data._id; // unique id of source (identity:address) 
              }).sort((a,b)=>{
                // newest at top 
                return a.data.data.createdAt < b.data.data.createdAt;
              });


              this.setState({
                myPosts,
                // fetchingMine: false
              })


            } catch(err){
              console.error('Failed getting local nodes', err);
            }


            this.setState({
              fetchingRemote: false
            });


          });

        }

        @autobind
        async handleCreatePost(){

          let text = this.state.postText;
          if(!text){
            return false;
          }

          let postGroupNodeId = this.state.postGroupNodeId;
          if(!postGroupNodeId){
            WINDOW.alert('Group required');
            return false;
          }

          let groupNode = this.props.groups.find(n=>{
            return n._id == postGroupNodeId;
          });

          console.log('State:', this.state);

          let sharedNode = {
            type: 'post:Qmskldfjf',
            data: {
              in_reply_to: null,
              thread_ref: universe.uuidv4(),
              message: text,
              author: this.props.state.OwnerSecondExternalIdentityNode.data.identity, // "idtest:nick"
              createdAt: Date.now(), // utc milliseconds since epoch 
              //visibility: 'public' // TODO: give simple direction on how to spread this data, if allowed 
              // usageLicense: "Qmsdfljfl..." // provide a usage license with your created content? 
              // TODO: verified/signed timestamp for this message (3rd party) 
            }
          }

          // should be OK to contain a code template 
          let permissionsNode = {
            type: 'syndication_permissions:Qmsdfkjsl2fj9vxc',
            data: {
              rules: [{
                type: 'allow_group:Qmsfkdljodsnkv',
                data: {
                  groupKey: groupNode.data.key
                }
              }]
            }
          };

          let syndicateToNode = {}; // done automatically, but could also provide a "specifically syndicate this data somewhere" node ? 

          let saveSharedNode = await this.createNodeOnSecond(sharedNode, permissionsNode, syndicateToNode);

          console.log('saveSharedNode',saveSharedNode);

          // // refetch 
          // this.fetchMine();


        }

        @autobind
        async addReply(note){

          let text = WINDOW.prompt('', '');
          if(!text){
            return false;
          }

          console.log('State:', this.state);

          console.log('Post to reply to:', note); 

          let sharedNode = {
            type: 'post:Qmskldfjf',
            data: {
              in_reply_to: note.data._id, // identity:address
              thread_ref: note.data.data.thread_ref,
              message: text,
              author: this.props.state.OwnerSecondExternalIdentityNode.data.identity, // "idtest:nick"
              createdAt: Date.now(), // utc milliseconds since epoch 
              // TODO: verified/signed timestamp for this message (3rd party) 
            }
          }

          console.log('sharedNode',sharedNode);

          // should be OK to contain a code template 
          let permissionsNode = {};

          let syndicateToNode = {};

          let saveSharedNode = await this.createNodeOnSecond(sharedNode, permissionsNode, syndicateToNode);

          console.log('saveSharedNode',saveSharedNode);

          // // refetch 
          // this.fetchMine();


        }

        @autobind
        createNodeOnSecond(sharedNode, permissionsNode, syndicateToNode){
          return new Promise(async (resolve, reject) => {

            console.log('NewNode:', sharedNode);

            // Make request to save Node on remote 
            try {

              let response = await universe.loadAndRunCapability('TalkToSecond',{},{
                type: 'standard_capability_action:0.0.1:local:298j291bs',
                data: {
                  action: 'send',
                  options: {
                    ExternalIdentityNode: this.props.state.OwnerSecondExternalIdentityNode,
                    RequestNode: {
                      type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
                      data: {
                        actions: [
                          // TODO: use identity token 
                          {
                            matchActionType: 'create_and_syndicate:Qmwlekjfwl',
                            dataForAction: {
                              type: 'create_and_syndicate_data:Qmskfjwoesjf',
                              data: {
                                sharedNode, // should include author=Second 
                                permissionsNode, // who can access this, get syndicated also 
                                syndicateToNode // where to syndicate this sharedNode  
                              }
                            }
                          }
                        ]

                      }
                    }
                  }
                }
              });

              console.log('create_and_syndicate response:', response);

              // get the created node 
              let savedNode = response.data.actionResponses[0];

              // console.log('Saved Node:', savedNode);

              try {
                universe.EE.emit('nodes-modified', '');
              }catch(err){
                console.error(err);
              }

              return resolve(savedNode);

              // this.setState({
              //   saving: false
              // })

            } catch(err){
              console.error('Failed getting local nodes');

              // alert('Failed');

              // this.setState({
              //   saving: false
              // })

            }

          })
        }

        @autobind
        handleUpdatePost(){
        }

        @autobind
        handleRemovePost(){
        }

        @autobind
        handleUpdatePostText(e){
          this.setState({
            postText: e.target.value
          });
        }

        @autobind
        handleUpdateSyndicationSearchTarget(e){
          // TODO: add multiple groups, identities, deny rules, etc. ! 
          this.setState({
            searchIdentity: e.target.value
          });
        }

        @autobind
        handleUpdateViewIdentityType(e){
          // TODO: add multiple groups, identities, deny rules, etc. ! 
          this.setState({
            viewIdentityType: e.target.value
          });
        }


        @autobind
        handleUpdatePostGroup(e){
          // TODO: add multiple groups, identities, deny rules, etc. ! 
          this.setState({
            postGroupNodeId: e.target.value
          });
        }

        @autobind
        renderPosts(){
          // console.log('Render Posts:',this.state.myPosts);
          return this.state.myPosts.map((note,idx)=>{
            let inReplyTo;
            // console.log('inReplyTo', note, note.data.data.in_reply_to);
            if(note.data.data.in_reply_to){
              inReplyTo = this.state.myPosts.find(p=>{return p.data._id == note.data.data.in_reply_to})
              if(!inReplyTo){
                // haven't loaded in-reply-to yet 
                // - trigger load? 
                inReplyTo = false;
              }
            }
            // console.log('inReplyTo', inReplyTo, note.data.data.in_reply_to);
            return (
              <div key={note._id} style={{paddingBottom:"8px", width:"380px"}}>
                <div className="box" onClick={e=>this.handleUpdatePost(idx)} style={{position:'relative'}}>
                  <span className="delete" style={styles.deleteIcon} onClick={e=>this.handleRemovePost(e, idx)}></span>
                  {
                    (inReplyTo === undefined) ? '':
                    <div>
                      {
                        inReplyTo ? 
                        <div>
                           Replied to @{inReplyTo.data.data.author.split(':')[1]}
                        </div>
                        :
                        <div>
                           Replied to [load required]
                        </div>
                      }
                    </div>
                  }
                  <div>
                    {
                      note.data.data.message
                    }
                  </div>
                  <div>
                    - {
                      note.data.data.author.split(':').slice(1).join(':')
                    }
                  </div>
                  <div>
                    <small>{note.data.data.createdAt}</small>
                  </div>
                  <div>
                    <button className="button is-info" onClick={e=>this.addReply(note)}>
                      + Reply 
                    </button>
                  </div>
                </div>
              </div>
            )
          })
        }

        render(){

          return (
            <div className="columns">
              <div className="column is-6">

                <br />

                <div className="field has-addons">
                  <div className="control">
                    <button className={"button is-default"} onClick={e=>this.fetchFromRemoteForIdentity(this.props)}>
                      Fetch Updates
                    </button>
                  </div>
                  {
                    !this.state.fetchingRemote ? '':
                    <div className="control">
                      <a className="button is-loading">
                        &nbsp;
                      </a>
                    </div>
                  }
                </div>


                {/* Posting */}
                <div className="field">
                  <div className="control">
                    <textarea className="textarea" value={this.state.postText} onChange={this.handleUpdatePostText} disabled={this.state.isPosting} />
                  </div>
                </div>
                <div className="level">
                  <div className="level-left">
                    <div className="level-item">

                      <button className={"button is-info " + (this.state.isPosting ? 'is-loading':'')} onClick={this.handleCreatePost}>
                        + Post
                      </button>

                    </div>
                    <div className="level-item">


                      <div className="field">
                        <div className="control">
                          <div className="select">
                            <select onChange={this.handleUpdatePostGroup} value={this.state.postGroupNodeId}>
                              <option>Audience:</option>
                              {
                                this.props.groups.map(groupNode=>(
                                  <option key={groupNode._id} value={groupNode._id}>{groupNode.data.name}</option>
                                ))
                              }
                              {
                                (!this.props.allExternalConnections.length && this.state.fetchingConnections) ?
                                <option>Loading groups</option>
                                :''
                              }
                            </select>
                          </div>
                        </div>
                      </div>

                    </div>
                  </div>

                </div>


                <hr />

                {/*
                <div className="field has-addons">
                  <div className="control">
                    <button className="button" onClick={this.fetchFromRemoteForIdentity}>
                      Fetch Latest
                    </button>
                  </div>
                  {
                    !this.state.fetchingRemote ? '':
                    <div className="control">
                      <a className="button is-loading">
                        &nbsp;
                      </a>
                    </div>
                  }
                </div>
                */}


                <br />

                <div>
                  {
                    this.renderPosts()
                  }
                </div>

              </div>

              <div className="column is-6">
                {/* selected conversation here? */}
              </div>

            </div>
          )
        }
      }

      mycomponent = universe.ReactGlobalState(mycomponent);
      mycomponent = universe.ReactHelpers(mycomponent);

      const styles = {
        deleteIcon: {
          position: 'absolute',
          top: '0px',
          right: '-24px',
          cursor: 'pointer'
        }
      }

      resolve({
        type: 'react_component:0.0.1:local:98912hd89',
        data: mycomponent
      });

    }catch(err){
      reject({
        ERROR: true, 
        err: err.toString()
      });
    }

  })


})()
