        @autobind
        renderPosts(){
          return this.state.myPosts.map((note,idx)=>{
            let inReplyTo;
            // console.log('inReplyTo', note, note.data.data.in_reply_to);
            if(note.data.data.in_reply_to){
              inReplyTo = this.state.myPosts.find(p=>{return p.data._id == note.data.data.in_reply_to})
              if(!inReplyTo){
                // haven't loaded in-reply-to yet 
                // - trigger load? 
                inReplyTo = false;
              }
            }
            // console.log('inReplyTo', inReplyTo, note.data.data.in_reply_to);
            return (
              <div key={note._id} style={{paddingBottom:"8px", width:"380px"}}>
                <div className="box" onClick={e=>this.handleUpdatePost(idx)} style={{position:'relative'}}>
                  <span className="delete" style={styles.deleteIcon} onClick={e=>this.handleRemovePost(e, idx)}></span>
